INSERT INTO `t_answers` (`id`, `fk_question`, `fk_user`, `answer`, `total_like_number`, `created_at`, `deleted_at`, `delete`) VALUES
(19, 4, 23, 'Đi nhổ răng đi', 1, '2015-01-22 13:51:45', NULL, 0),
(20, 5, 43, 'Nhổ nhổ nhổ', 1, '2015-02-03 23:11:35', NULL, 0),
(21, 5, 23, 'Nhổ tiếp đi', 1, '2015-02-06 18:37:03', NULL, 0),
(22, 5, 28, 'Răng bị xỉn màu do nhiều nguyên nhân. Tùy từng nguyên nhân mà có cách giải quyết khác nhau. Nếu răng bị xỉn màu ngay sau khi răng mọc, rất khó để làm cho răng trắng sáng như bình thường, trong trường hợp này để có hàm răng trắng sáng, bạn có thể cần tới can thiệp của phục hình thẩm mỹ, nếu xỉn răng do mắc phải  như hút thuốc lá, cà phê, bạn có thể nhờ tới tẩy trắng răng.', 3, '2015-02-07 20:58:07', NULL, 0),
(23, 7, 28, 'Khoảng 6 tuổi bé sẽ mọc chiếc răng vĩnh viễn đầu tiên, một số trẻ mọc răng sớm hơn một chút. Nếu bé có kèm theo các dấu hiệu như sốt nhẹ, biếng ăn, chảy dãi và những răng khác trên cung hàm không có triệu chứng giống như vậy thì nhiều khả năng bé đang sắp có thêm một chiếc răng mới đấy mẹ. Cho bé đi khám tại nha sĩ để kiểm tra chắc chắn mẹ nhé :3', 3, '2015-02-11 21:50:01', NULL, 0),
(24, 6, 28, 'Bạn không nói rõ là bạn bao nhiêu tuổi, nếu bạn trong độ tuổi < 30 tuổi thì nắn chỉnh răng là giải pháp lâu dài cho răng của bạn. Nếu bạn không  muốn nắn chỉnh, có thể tham khảo về làm răng thẩm mỹ bằng phục hình nhưng không phải trường hợp nào cũng cho kết quả như ý muốn đâu bạn nhé. ', 2, '2015-02-11 21:54:05', NULL, 0),
(25, 4, 28, 'Răng khôn (răng 8) thì mới nhổ nhé, nếu không có răng 8 mà đó lại là răng số 7 thì phải "cố gắng" bảo tồn nhé :D', 3, '2015-02-11 22:14:35', NULL, 0),
(26, 8, 39, '25 tuổi nằm trong thời gian mọc răng khôn (em có thể tham khảo ở mục Tuổi mọc răng vĩnh viễn thuộc phần Tư vấn của dento.vn). Không thấy răng xuất hiện trong miệng, không có nghĩa là không có răng mọc ngầm bên dưới. Tuy nhiên, cũng không loại trừ các trường hợp bệnh lý khác như u hoặc nang xương hàm... Vì thế em cần đi khám, chụp thêm phim XQuang răng để biết chính xác nhé.', 1, '2015-02-13 23:18:53', NULL, 0),
(27, 9, 39, 'Tin vui là phần lớn đều có cảm giác giống như em :D, đặc biệt trong trường hợp răng của em lệch lạc nhiều hoặc nhổ răng thì cảm giác khó chịu thường nhiều hơn những trường hợp nhẹ. Tuy nhiên đau và khó chịu nhiều cũng chỉ diễn ra trong 1-2 tuần đầu tiên. Nếu kéo dài và đau không giảm, thậm chí tăng dần thì em cũng nên đi kiểm tra lại nhé.', 0, '2015-02-15 11:06:11', NULL, 0),
(28, 10, 39, 'Vùng lợi răng cửa của bạn có từng hoặc đang bị sưng hoặc viêm không? Răng đổi màu rõ thường liên quan đến bệnh lý tủy hoại tử hay còn gọi là chết tủy do sang chấn. Nhiều khả năng bạn sẽ phải lấy bỏ tủy răng chết, sau khi điều trị hàn ống tủy ổn định, nha sĩ sẽ tiến hành tẩy trắng tại chỗ và làm chụp răng bên ngoài để đảm bảo về thẩm mỹ cũng như tránh nguy cơ răng bị gãy vỡ sau này bạn nhé.', 0, '2015-02-15 11:13:26', NULL, 0),
(29, 12, 39, 'Bạn nên cho bé đi khám và tư vấn tại các nha sĩ chuyên ngành chỉnh nha. Tại độ tuổi này, bé có thể đeo một số dụng cụ hướng dẫn phù hợp để lấy lại tương quan đúng giữa hàm trên và hàm dưới. Chồng bạn có hàm dưới hô ra trước nhiều, thường hàm dưới hô ra trước có liên quan đến sự phát triển của xương nên con bạn có thể "thừa hưởng" sự di truyền này, tuy nhiên, cũng có thể do thói quen hay đưa hàm ra trước của bé. Bạn có thể tham khảo thêm bài viết về Khí cụ chỉnh nha ngoài mặt trong chuyên mục Sản phẩm của dento.vn để biết thêm bạn nhé.', 5, '2015-02-15 13:01:19', NULL, 0),
(30, 13, 28, 'Để an tâm bạn có thể đến các Bệnh viện chuyên khoa hoặc các phòng khám lớn có uy tín để làm bạn nhé.', 3, '2015-02-23 22:25:16', NULL, 0),
