(4, NULL, 'lethanhan.bkaptech@gmail.com', 'Lê thanh an ', 'Bị đau răng trong cùng bên phải trên ', NULL, NULL, 30, 'NORMAL', '2015-01-22 13:49:04', NULL, 0),
(5, NULL, 'hai_capital102@yahoo.com', 'Bui Manh Hai', 'Răng bị xỉn màu thì phải làm sao?', NULL, NULL, 50, 'NORMAL', '2015-02-01 20:36:46', NULL, 0),
(6, NULL, 'ngocthuy5443@yahoo.com', 'Thùy Vân', 'Răng em bị khấp khểnh nhưng không muốn đeo nắn chỉnh răng vì thời gian dài và vướng víu lắm, có cách nào không cả nhà :/', NULL, NULL, 13, 'NORMAL', '2015-02-08 11:19:30', NULL, 0),
(7, NULL, 'ruby_211290@yahoo.com', 'ngọc', 'Con tôi 5 tuổi, mấy hôm nay bé quấy khóc nhiều, lợi bên trong cùng hàm dưới bên phải bị đỏ nhiều, có phải là do mọc răng không ạh?', NULL, NULL, 21, 'NORMAL', '2015-02-08 15:08:25', NULL, 0),
(8, NULL, 'admin@cib.com.vn', 'Luong Manh Huy', 'Em năm nay 25 tuổi, mấy hôm nay đau nhiều răng trong cùng bên phải ở hàm dưới, trong miệng em không thấy có răng mọc thêm, xin cho em tư vấn với ạh?', NULL, NULL, 16, 'NORMAL', '2015-02-11 22:00:13', NULL, 0),
(9, NULL, 'atkvina@gmail.com', 'Tạ Thành Công', 'Em mới deo hàm nắn chỉnh cố định được vài ngày nhưng rất khó chịu. Bác sĩ bảo không sao, nhưng bạn em đeo thì bảo là không bị như thế. Có ai đã từng đeo mà bị giống như em không ạ?', NULL, NULL, 5, 'NORMAL', '2015-02-14 11:35:04', NULL, 0),
(10, NULL, 'nguyenminhsnp@gmail.com', 'Nguyễn văn Minh ', 'Em bị ngã xe máy từ năm 20 tuổi, bị đập vùng răng cửa xuống đường, năm nay em 25 tuổi, 2 răng cừa trên của em ngày càng bị sậm màu rất rõ so với răng bên cạnh, không biết là nên khắc phục như thế nào ạ?', NULL, NULL, 4, 'NORMAL', '2015-02-14 13:40:03', NULL, 0),
(11, NULL, 'nguyenminhsnp@gmail.com', 'Nguyễn văn Minh ', 'Em bị ngã xe máy từ năm 20 tuổi, bị đập vùng răng cửa xuống đường, năm nay em 25 tuổi, 2 răng cừa trên của em ngày càng bị sậm màu rất rõ so với răng bên cạnh, không biết là nên khắc phục như thế nào ạ?', NULL, NULL, 5, 'NORMAL', '2015-02-14 13:40:05', NULL, 0),
(12, NULL, 'chaoxme@yahoo.com.vn', 'Trần Minh Hạnh', 'Chồng tôi bị răng hàm dưới hô ra trước nhiều, con trai tôi mới 8 tuổi mà cũng có hiện tượng hàm dưới bị đẩy về phía trước. Liệu con tôi có nguy cơ bị hô hàm dưới ra trước sau này không ạh, có cách gì để ngăn ngừa không ạh?', NULL, NULL, 29, 'NORMAL', '2015-02-15 11:18:35', NULL, 0),
(13, NULL, 'hoalyxinh@yahoo.com', 'Nguyễn Thị Phương Ly', 'Tôi đã đi khám răng nhiều lần và được chẩn đoán là răng bị nhiễm Flo nên răng bị tối màu, bề mặt nhám. Tôi muốn đi làm lại hàm răng thì nên đi làm tại phòng khám nào nhỉ?', NULL, NULL, 27, 'NORMAL', '2015-02-20 23:48:31', NULL, 0),
(14, NULL, 'mua nuoc xuc mieng o dau', NULL, NULL, NULL, NULL, 9, 'NORMAL', '2015-02-24 14:16:27', '2015-03-17 23:51:23', 1),
(15, NULL, 'vanan@yahoo.com', 'Nguyễn Vân Anh', 'Con tôi 5 tuổi, hiện tại có 2 răng hàm phía dưới bên trái, sâu đen lớn, trước bị đau một vài lần nhưng lại tự khỏi, không biếtcó ảnh hưởng gì đến mọc răng sau này của bé không?', NULL, NULL, 28, 'NORMAL', '2015-03-01 13:20:57', NULL, 0),
(16, NULL, 'lethanhan.bkaptech@gmail.com', 'Le thanh an ', '123456789', NULL, NULL, 6, 'NORMAL', '2015-03-04 18:41:48', '2015-03-17 23:51:29', 1),
(17, NULL, 'anhnp@gmail.com', 'Nguyễn Phương Anh', 'Răng của tôi bị lõm ở phần răng tiếp giáp với lợi các răng phía trong hàm trên và hàm dưới, chải răng rất buốt, xin cho lời khuyên?', NULL, NULL, 25, 'NORMAL', '2015-03-28 09:37:51', NULL, 0);
